content.css:
:root {
scrollbar-size: 100px;
color-background: #ffffff;
}


.knob-strip .knob-container {
right: auto;
left: 10px;
border-radius: 37rem / 45rem;
background: var(--color-background);
}
.knob-strip .input-container {
  z-index: 0;
  --alpha-stroke: 0;
}

.text-container {
  --widget-padding: -1rem;
  --alpha-stroke: 0;
  --alpha-fill-off: 0.1;
}
.modal-container,
.button-container {
  --alpha-stroke: 0;
  --alpha-fill-off: 0.15;
  --widget-padding: -1rem;
}

.input-container {
  --alpha-stroke: 0; 
  --alpha-fill-off: 0;
}
.button-container.no-pulse inner:after {
  animation: none
}

.flat-btns .button-container {
--alpha-stroke:0.5;
--alpha-fill-off: 0.05;
--color-text: rgba(160,160,200,1);
--color-stroke: var(--color-text);
}

.flat-btns .button-container.on {
--alpha-stroke:1;
--alpha-fill-on: 0.2;
--color-text-on: var(--color-widget);
--color-stroke: var(--color-widget)
}

